            var vacatureId = button.getAttribute('data-vacature-id');
            document.getElementById('vacature-id-field').value = vacatureId; // Zet het ID in een hidden field in je formulier
        });
    });
});
</script>

<script>
document.addEventListener('DOMContentLoaded', function () {
    var buttons = document.querySelectorAll('button[data-vacature-id]');

    buttons.forEach(function (button) {
        button.addEventListener('click', function () {
            var vacatureId = button.getAttribute('data-vacature-id');
            var formField = document.querySelector('#input_4_6'); // Het ID van het Gravity Forms veld (vervang dit met het juiste ID)
            if (formField) {
                formField.value = vacatureId;
            }
        });
    });
});
</script>

<script>
document.addEventListener('DOMContentLoaded', function() {
    // Haal de huidige URL op
    var currentUrl = window.location.href;

    // Zoek alle menu-items
    var menuItems = document.querySelectorAll('.menu-item');

    // Loop door alle menu-items
    menuItems.forEach(function(item) {
        if (item.href === currentUrl) {
            // Voeg de class 'active' toe als de URL's overeenkomen
            item.classList.add('active');
        }
    });
});
</script>


